/*------------------------------------------------------                 INVIGEN beta v1.0 Author: xtrung.net@gmail.com Write On: 04/27/2008 Create On:12/15/2011 11:16:24 PM ------------------------------------------------------*/ using INVI.DataAccess.Base; using INVI.Entity; using System; using System.Data; using System.Data.SqlClient; using System.Configuration; using System.Collections.Generic; using System.Collections; using System.Web;  namespace INVI.DataAccess {         public class MucdoDAL : SqlProvider<MucdoEntity>     {         static MucdoDAL()         {             InitReader();         }         protected static void InitReader()         {             getFromReader=delegate(SqlDataReader dr)             {                 MucdoEntity entity = new MucdoEntity(); 				entity.PK_iMucdoID = Int32.Parse("0"+dr["PK_iMucdoID"].ToString()); 				entity.sTenmucdo = dr["sTenmucdo"].ToString();                 return entity;             };         }         public static MucdoEntity GetOne(Int32 PK_iMucdoID)         {             string cmdName = "spMucdo_GetByPK";             SqlParameter p = new SqlParameter("@PK_iMucdoID", PK_iMucdoID);             MucdoEntity entity = GetOne(cmdName, p);             return entity;         }         public static List<MucdoEntity> GetAll()         {             string cmdName = "spMucdo_Get";             return GetList(cmdName);         }                  public static int Add(MucdoEntity entity)         {             string cmdName = "spMucdo_Insert";             return Run(cmdName,true,initParams(entity));          }         public static bool Edit(MucdoEntity entity)         {             string cmdName = "spMucdo_Update";             return Run(cmdName,false,initParams(entity))>0;         }         public static bool Remove(Int32 PK_iMucdoID)         {             string cmdName = "spMucdo_Delete";             SqlParameter p = new SqlParameter("@PK_iMucdoID", PK_iMucdoID);             return Run(cmdName,false,p)>0;         }         #region private         private static SqlParameter[] initParams(MucdoEntity entity)         {             SqlParameter[] p = new SqlParameter[2]; 			p[0] = new SqlParameter("@PK_iMucdoID", entity.PK_iMucdoID); 			p[1] = new SqlParameter("@sTenmucdo", entity.sTenmucdo);             return p;         }         #endregion             } }