/*------------------------------------------------------                 INVIGEN beta v1.0 Author: xtrung.net@gmail.com Write On: 04/27/2008 Create On:12/15/2011 11:16:18 PM ------------------------------------------------------*/ using System; using System.Collections.Generic; using System.Text; using System.Reflection; namespace INVI.Entity {     public class MucdoEntity     {         public MucdoEntity()         { 			m_pk_imucdoid=0; 			m_stenmucdo="";         } 		private Int32 m_pk_imucdoid; 		public Int32 PK_iMucdoID 		{ 			get { return m_pk_imucdoid ; } 			set { m_pk_imucdoid = value; } 		} 		private String m_stenmucdo; 		public String sTenmucdo 		{ 			get { return m_stenmucdo ; } 			set { m_stenmucdo = value; } 		}          #region Comparison         public static List<MucdoEntity> Sort(List<MucdoEntity> list, String SortExpression, String SortDirection)         {             string strComparisonAttrb="COMPARISON_"+SortExpression;             PropertyInfo propInfo= typeof(MucdoEntity).GetProperty(strComparisonAttrb);             if (propInfo != null)             {                 list.Sort(propInfo.GetGetMethod().Invoke(null,null) as Comparison<MucdoEntity>);                 if (SortDirection == "DESC")                     list.Reverse();             }             return list;         }          		public static Comparison<MucdoEntity> COMPARISON_PK_iMucdoID 		{ 			get 			{ 				return delegate(MucdoEntity entity,MucdoEntity other) 				{ 					return entity.PK_iMucdoID.CompareTo(other.PK_iMucdoID); 				}; 			} 		} 		public static Comparison<MucdoEntity> COMPARISON_sTenmucdo 		{ 			get 			{ 				return delegate(MucdoEntity entity,MucdoEntity other) 				{ 					return entity.sTenmucdo.CompareTo(other.sTenmucdo); 				}; 			} 		}         #endregion     } } 